== JWT Generator
[source,json] function base64url(source) { // Encode in classical base64 encodedSource = CryptoJS.enc.Base64.stringify(source);
// Remove padding equal characters encodedSource = encodedSource.replace(/=+$/, &ldquo;);
// Replace characters according to base64url specifications encodedSource = encodedSource.replace(/+/g, &lsquo;-&rsquo;); encodedSource = encodedSource.replace(/\//g, &lsquo;_&lsquo;);
return encodedSource; }
